mapper.select:
SELECT count(*) FROM brd_freeboard a LEFT JOIN cod_freeboard b ON a.frid = b.frid LEFT JOIN info_userinfo c ON a.userno = c.userno WHERE b.ptypeid= #{ptypeid}
AND a.title LIKE CONCAT('%',#{keyword},'%')
AND c.nickname LIKE CONCAT('%',#{keyword},'%')
AND a.frid = #{frid}

SELECT b.brdid, b.frid, d.name as typename, b.title, CASE WHEN b.regdate > CURDATE() THEN DATE_FORMAT(b.regdate, '%H:%i:%s') ELSE DATE_FORMAT(b.regdate, '%Y-%m-%d') END as regdate, a.nickname as writer, b.count, (SELECT COUNT(*) FROM brd_freeboard_likes f WHERE b.brdid = f.brdid and f.likes = 1) as likes, (SELECT COUNT(*) FROM brd_freeboard_reply c WHERE c.brdid = b.brdid) as repcount, b.chk_anonymous FROM brd_freeboard b LEFT JOIN info_userinfo a on b.userno = a.userno LEFT JOIN cod_freeboard d on b.frid = d.frid WHERE 1=1
AND d.ptypeid = #{ptypeid}
AND b.title LIKE CONCAT('%',#{keyword},'%')
AND a.nickname LIKE CONCAT('%',#{keyword},'%')
AND b.frid = #{frid}
ORDER BY b.brdid DESC LIMIT #{skiprow}, #{pagerow}

mapper.update:
UPDATE brd_freeboard SET
	count = count + 1
WHERE brdid = #{brdid}

UPDATE brd_freeboard_likes SET
	likes = likes + 1
WHERE brdid = #{brdid}
	AND userno = #{userno}

UPDATE brd_freeboard_likes SET
	dislikes = dislikes + 1
WHERE brdid = #{brdid}
	AND userno = #{userno}

UPDATE brd_freeboard_reply_likes SET
	likes = likes + 1
WHERE repid = #{repid}
	AND userno = #{userno}

UPDATE brd_freeboard_reply_likes SET
	dislikes = dislikes + 1
WHERE repid = #{repid}
	AND userno = #{userno}

UPDATE brd_freeboard SET
	content = #{content},
	title = #{title},
	moddate = NOW(),
	frid = #{frid}
WHERE brdid = #{brdid}

mapper.delete:
DELETE FROM brd_freeboard_reply
WHERE brdid = #{brdid}

	AND repid = #{repid}
