mapper.update:
UPDATE brd_freeboard SET
	count = count + 1
WHERE brdid = #{brdid}

UPDATE brd_freeboard SET
	likes = likes + 1
WHERE brdid = #{brdid}

UPDATE brd_freeboard SET
	dislikes = dislikes + 1
WHERE brdid = #{brdid}

UPDATE brd_freeboard_reply SET
	likes = likes + 1
WHERE repid = #{repid}

UPDATE brd_freeboard_reply SET
	dislikes = dislikes + 1
WHERE repid = #{repid}
