mapper.select:
SELECT a.brdid, a.orgid, b.name as orgname, b.orgtypeid, a.title, CASE WHEN b.regdate > CURDATE() THEN DATE_FORMAT(a.regdate, '%H:%i:%s') ELSE DATE_FORMAT(a.regdate, '%Y-%m-%d') END as regdate, c.korname, a.count, a.likes FROM brd_orgnization a LEFT JOIN info_organization b on a.orgid = b.orgid LEFT JOIN info_userinfo c on a.userno = c.userno WHERE b.orgtypeid = #{orgtypeid}
AND a.title LIKE CONCAT('%',#{keyword},'%')
AND c.nickname LIKE CONCAT('%',#{keyword},'%')
ORDER BY a.brdid DESC LIMIT #{skiprow}, #{pagerow}

mapper.update:
UPDATE brd_orgnization SET
	count = count + 1
WHERE brdid = #{brdid}

UPDATE brd_orgnization SET
	likes = likes + 1
WHERE brdid = #{brdid}

UPDATE brd_orgnization SET
	dislikes = dislikes + 1
WHERE brdid = #{brdid}

UPDATE brd_orgnization_reply SET
	likes = likes + 1
WHERE repid = #{repid}

UPDATE brd_orgnization_reply SET
	dislikes = dislikes + 1
WHERE repid = #{repid}
