mapper.update:
UPDATE brd_schoolwork SET
	count = count + 1
WHERE brdid = #{brdid}

mapper.select:
SELECT b.brdid, b.swid, d.name as typename, b.title, CASE WHEN b.regdate > CURDATE() THEN DATE_FORMAT(b.regdate, '%H:%i:%s') ELSE DATE_FORMAT(b.regdate, '%Y-%m-%d') END as regdate, a.nickname as writer, b.count, (SELECT COUNT(*) FROM brd_schoolwork_likes f WHERE b.brdid = f.brdid and f.likes = 1) as likes, (SELECT COUNT(*) FROM brd_schoolwork_reply c WHERE c.brdid = b.brdid) as repcount FROM brd_schoolwork b LEFT JOIN info_userinfo a on b.userno = a.userno LEFT JOIN cod_schoolwork d on b.swid = d.swid WHERE 1=1
AND d.ptypeid = #{ptypeid}
AND b.title LIKE CONCAT('%',#{keyword},'%')
AND a.nickname LIKE CONCAT('%', #{keyword}, '%')
AND b.swid = #{swid}
ORDER BY b.brdid DESC LIMIT #{skiprow}, #{pagerow}

mapper.update:
UPDATE brd_schoolwork SET
	count = count + 1
WHERE brdid = #{brdid}

mapper.select:
SELECT COUNT(*) FROM brd_schoolwork a LEFT JOIN cod_schoolwork b ON a.swid = b.swid LEFT JOIN info_userinfo c ON a.userno = c.userno WHERE b.ptypeid= #{ptypeid}
AND a.title LIKE CONCAT('%',#{keyword},'%')
AND c.nickname LIKE CONCAT('%',#{keyword},'%')
AND a.swid = #{swid}

SELECT
	id as lid,
	likes
FROM brd_schoolwork_likes
WHERE userno = #{userno}
	AND brdid = #{brdid}

mapper.insert:
INSERT INTO brd_schoolwork_likes(
	brdid,
	userno,
	likes
)VALUES(
	#{brdid},
	#{userno},
	#{likes}
)

mapper.update:
UPDATE brd_schoolwork SET
	likes = likes+1
WHERE brdid=#{brdid}
	AND userno = #{userno}

UPDATE brd_schoolwork_reply SET
	likes = likes + 1
WHERE repid = #{repid}
	AND userno = #{userno}

UPDATE brd_schoolwork_reply SET
	dislikes = dislikes+1
WHERE repid = #{repid}
	AND userno = #{userno}

mapper.delete:
DELETE FROM
	brd_schoolwork_reply
WHERE brdid = #{brdid}

	AND repid = #{repid}
